import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import TypewriterText from './TypewriterText';

const Hero = ({ theme }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Generate particles
    const generateParticles = () => {
      const newParticles = [];
      const particleCount = 30;

      for (let i = 0; i < particleCount; i++) {
        newParticles.push({
          id: i,
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          vx: (Math.random() - 0.5) * 0.5,
          vy: (Math.random() - 0.5) * 0.5,
          originalX: Math.random() * window.innerWidth,
          originalY: Math.random() * window.innerHeight,
        });
      }
      setParticles(newParticles);
    };

    generateParticles();

    // Animation loop
    const animateParticles = () => {
      setParticles(prevParticles => 
        prevParticles.map(particle => {
          let newX = particle.x + particle.vx;
          let newY = particle.y + particle.vy;

          // Keep particles within bounds
          if (newX < 0 || newX > window.innerWidth) particle.vx *= -1;
          if (newY < 0 || newY > window.innerHeight) particle.vy *= -1;

          return {
            ...particle,
            x: Math.max(0, Math.min(window.innerWidth, newX)),
            y: Math.max(0, Math.min(window.innerHeight, newY)),
          };
        })
      );
    };

    const animationId = setInterval(animateParticles, 50);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearInterval(animationId);
    };
  }, []);

  const scrollToAbout = () => {
    const element = document.getElementById('about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section className="hero-section">
      {/* Main cursor follower */}
      <motion.div 
        className="cursor-follow-main"
        animate={{
          x: mousePosition.x - 100,
          y: mousePosition.y - 100,
        }}
        transition={{
          type: "spring",
          stiffness: 100,
          damping: 20,
        }}
      />

      {/* Secondary cursor follower */}
      <motion.div 
        className="cursor-follow-secondary"
        animate={{
          x: mousePosition.x - 150,
          y: mousePosition.y - 150,
        }}
        transition={{
          type: "spring",
          stiffness: 60,
          damping: 25,
        }}
      />

      {/* Floating geometric shapes */}
      <motion.div 
        className="floating-shape shape-1"
        animate={{
          x: mousePosition.x * 0.1,
          y: mousePosition.y * 0.1,
          rotate: mousePosition.x * 0.5,
        }}
        transition={{
          type: "spring",
          stiffness: 30,
          damping: 20,
        }}
      />

      <motion.div 
        className="floating-shape shape-2"
        animate={{
          x: mousePosition.x * -0.15,
          y: mousePosition.y * -0.15,
          rotate: mousePosition.y * 0.3,
        }}
        transition={{
          type: "spring",
          stiffness: 25,
          damping: 18,
        }}
      />

      <motion.div 
        className="floating-shape shape-3"
        animate={{
          x: mousePosition.x * 0.2,
          y: mousePosition.y * -0.1,
          rotate: mousePosition.x * -0.4,
        }}
        transition={{
          type: "spring",
          stiffness: 35,
          damping: 22,
        }}
      />

      <div className="hero-content">
        <motion.h1
          className="hero-name"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Nyan Aung
        </motion.h1>

        <motion.div
          className="hero-subtitle"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <TypewriterText 
            texts={[
              "Full-Stack Engineer",
              "Software Engineer",
              "Backend Engineer",
              "Frontend Engineer"
            ]}
            speed={80}
            deleteSpeed={40}
            pauseTime={2000}
          />
        </motion.div>
      </div>

      <motion.div
        className="scroll-indicator"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.5 }}
        onClick={scrollToAbout}
      >
        <motion.div
          className="scroll-arrow"
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          â†“
        </motion.div>
      </motion.div>

      <style jsx>{`
        .hero-section {
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          position: relative;
          background: linear-gradient(135deg, var(--gradient-start) 0%, var(--gradient-end) 100%);
          overflow: hidden;
        }

        .cursor-follow-main {
          position: absolute;
          width: 200px;
          height: 200px;
          background: linear-gradient(45deg, rgba(37, 99, 235, 0.3), rgba(96, 165, 250, 0.2));
          border-radius: 50%;
          pointer-events: none;
          z-index: 1;
          filter: blur(20px);
          box-shadow: 0 0 50px rgba(37, 99, 235, 0.4);
        }

        .cursor-follow-secondary {
          position: absolute;
          width: 300px;
          height: 300px;
          background: radial-gradient(circle, rgba(59, 130, 246, 0.15) 0%, rgba(147, 197, 253, 0.1) 50%, transparent 70%);
          border-radius: 50%;
          pointer-events: none;
          z-index: 1;
          filter: blur(30px);
        }

        .floating-shape {
          position: absolute;
          pointer-events: none;
          z-index: 2;
        }

        .shape-1 {
          width: 60px;
          height: 60px;
          background: linear-gradient(135deg, rgba(37, 99, 235, 0.4), rgba(96, 165, 250, 0.3));
          border-radius: 20% 80% 20% 80%;
          top: 20%;
          left: 20%;
          animation: floatShape1 6s ease-in-out infinite;
        }

        .shape-2 {
          width: 40px;
          height: 40px;
          background: linear-gradient(45deg, rgba(59, 130, 246, 0.5), rgba(147, 197, 253, 0.3));
          clip-path: polygon(50% 0%, 0% 100%, 100% 100%);
          top: 60%;
          right: 25%;
          animation: floatShape2 8s ease-in-out infinite reverse;
        }

        .shape-3 {
          width: 80px;
          height: 80px;
          background: linear-gradient(225deg, rgba(37, 99, 235, 0.3), rgba(59, 130, 246, 0.4));
          border-radius: 50% 20% 50% 20%;
          bottom: 30%;
          left: 60%;
          animation: floatShape3 7s ease-in-out infinite;
        }

        @keyframes floatShape1 {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(180deg); }
        }

        @keyframes floatShape2 {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(15px) rotate(-90deg); }
        }

        @keyframes floatShape3 {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-25px) rotate(90deg); }
        }

        .hero-content {
          text-align: center;
          z-index: 10;
          position: relative;
        }

        .hero-name {
          font-family: 'Poppins', sans-serif;
          font-size: 80px;
          font-weight: 700;
          color: var(--text-light);
          margin-bottom: 20px;
          background: linear-gradient(45deg, var(--text-light), var(--accent-blue));
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .hero-subtitle {
          font-family: 'Inter', sans-serif;
          font-size: 24px;
          font-weight: 300;
          color: var(--text-light);
          opacity: 0.8;
          max-width: 600px;
          margin: 0 auto;
          min-height: 30px;
        }

        .typewriter-text {
          display: inline-block;
        }

        .cursor {
          animation: blink 1s infinite;
          color: var(--accent-blue);
          font-weight: 400;
        }

        @keyframes blink {
          0%, 50% { opacity: 1; }
          51%, 100% { opacity: 0; }
        }

        .scroll-indicator {
          position: absolute;
          bottom: 30px;
          left: 50%;
          transform: translateX(-50%);
          cursor: pointer;
          color: var(--text-light);
          opacity: 0.7;
        }

        .scroll-arrow {
          font-size: 24px;
          font-weight: 300;
        }

        @media (max-width: 768px) {
          .hero-name {
            font-size: 50px;
          }

          .hero-subtitle {
            font-size: 18px;
            padding: 0 20px;
          }

          .scroll-indicator {
            bottom: 20px;
          }

          .scroll-arrow {
            font-size: 20px;
          }
        }

        @media (max-width: 480px) {
          .hero-name {
            font-size: 40px;
          }

          .hero-subtitle {
            font-size: 16px;
          }
        }
      `}</style>
    </section>
  );
};

export default Hero;
