import React, { useCallback } from 'react';
import { motion } from 'framer-motion';
import Particles from '@tsparticles/react';
import { loadSlim } from '@tsparticles/slim';
import TypewriterText from './TypewriterText';

const Hero = ({ theme }) => {
  const particlesInit = useCallback(async (engine) => {
    await loadSlim(engine);
  }, []);

  const scrollToAbout = () => {
    const element = document.getElementById('about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Calculate connections between particles
  const getConnections = () => {
    const connections = [];
    const maxDistance = 200;

    for (let i = 0; i < particles.length; i++) {
      for (let j = i + 1; j < particles.length; j++) {
        const dx = particles[i].x - particles[j].x;
        const dy = particles[i].y - particles[j].y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < maxDistance) {
          // Calculate cursor influence on connection strength
          const cursorDx1 = particles[i].x - mousePosition.x;
          const cursorDy1 = particles[i].y - mousePosition.y;
          const cursorDx2 = particles[j].x - mousePosition.x;
          const cursorDy2 = particles[j].y - mousePosition.y;
          const cursorDistance1 = Math.sqrt(cursorDx1 * cursorDx1 + cursorDy1 * cursorDy1);
          const cursorDistance2 = Math.sqrt(cursorDx2 * cursorDx2 + cursorDy2 * cursorDy2);

          // Boost opacity when cursor is near either particle
          const cursorInfluence = Math.max(
            Math.max(0, 1 - cursorDistance1 / 300),
            Math.max(0, 1 - cursorDistance2 / 300)
          );

          connections.push({
            x1: particles[i].x,
            y1: particles[i].y,
            x2: particles[j].x,
            y2: particles[j].y,
            opacity: Math.min(0.8, (1 - (distance / maxDistance)) + cursorInfluence * 0.4)
          });
        }
      }
    }
    return connections;
  };

  return (
    <section className="hero-section">
      {/* Particle Network Background */}
      <div className="particle-network">
        {/* Render connections */}
        <svg className="connections-svg">
          {getConnections().map((connection, index) => (
            <line
              key={index}
              x1={connection.x1}
              y1={connection.y1}
              x2={connection.x2}
              y2={connection.y2}
              className="connection-line"
              style={{ opacity: connection.opacity }}
            />
          ))}
        </svg>

        {/* Render particles */}
        {particles.map((particle) => {
          const dx = particle.x - mousePosition.x;
          const dy = particle.y - mousePosition.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          const influence = Math.max(0, 1 - distance / 250);

          return (
            <motion.div
              key={particle.id}
              className="particle-node"
              style={{
                left: particle.x,
                top: particle.y,
              }}
              animate={{
                x: dx * influence * 0.5,
                y: dy * influence * 0.5,
                scale: 1 + influence * 0.8,
              }}
              transition={{
                type: "spring",
                stiffness: 120,
                damping: 15,
              }}
            />
          );
        })}
      </div>

      <div className="hero-content">
        <motion.h1
          className="hero-name"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Nyan Aung
        </motion.h1>

        <motion.div
          className="hero-subtitle"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <TypewriterText 
            texts={[
              "Full-Stack Engineer",
              "Software Engineer",
              "Backend Engineer",
              "Frontend Engineer"
            ]}
            speed={80}
            deleteSpeed={40}
            pauseTime={2000}
          />
        </motion.div>
      </div>

      <motion.div
        className="scroll-indicator"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.5 }}
        onClick={scrollToAbout}
      >
        <motion.div
          className="scroll-arrow"
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          â†“
        </motion.div>
      </motion.div>

      <style jsx>{`
        .hero-section {
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          position: relative;
          background: linear-gradient(135deg, var(--gradient-start) 0%, var(--gradient-end) 100%);
          overflow: hidden;
        }

        .particle-network {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          pointer-events: none;
          z-index: 1;
        }

        .connections-svg {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          pointer-events: none;
        }

        .connection-line {
          stroke: var(--accent-blue);
          stroke-width: 1.5;
          opacity: 0.4;
          transition: opacity 0.2s ease;
        }

        .particle-node {
          position: absolute;
          width: 5px;
          height: 5px;
          background: var(--accent-blue);
          border-radius: 50%;
          pointer-events: none;
          box-shadow: 0 0 8px var(--accent-blue);
          transform: translate(-50%, -50%);
        }

        .hero-content {
          text-align: center;
          z-index: 10;
          position: relative;
        }

        .hero-name {
          font-family: 'Poppins', sans-serif;
          font-size: 80px;
          font-weight: 700;
          color: var(--text-light);
          margin-bottom: 20px;
          background: linear-gradient(45deg, var(--text-light), var(--accent-blue));
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .hero-subtitle {
          font-family: 'Inter', sans-serif;
          font-size: 24px;
          font-weight: 300;
          color: var(--text-light);
          opacity: 0.8;
          max-width: 600px;
          margin: 0 auto;
          min-height: 30px;
        }

        .typewriter-text {
          display: inline-block;
        }

        .cursor {
          animation: blink 1s infinite;
          color: var(--accent-blue);
          font-weight: 400;
        }

        @keyframes blink {
          0%, 50% { opacity: 1; }
          51%, 100% { opacity: 0; }
        }

        .scroll-indicator {
          position: absolute;
          bottom: 30px;
          left: 50%;
          transform: translateX(-50%);
          cursor: pointer;
          color: var(--text-light);
          opacity: 0.7;
        }

        .scroll-arrow {
          font-size: 24px;
          font-weight: 300;
        }

        @media (max-width: 768px) {
          .hero-name {
            font-size: 50px;
          }

          .hero-subtitle {
            font-size: 18px;
            padding: 0 20px;
          }

          .scroll-indicator {
            bottom: 20px;
          }

          .scroll-arrow {
            font-size: 20px;
          }
        }

        @media (max-width: 480px) {
          .hero-name {
            font-size: 40px;
          }

          .hero-subtitle {
            font-size: 16px;
          }
        }
      `}</style>
    </section>
  );
};

export default Hero;
