import React from 'react';
import { motion } from 'framer-motion';
import TypewriterText from './TypewriterText';

const Hero = ({ theme }) => {

  const scrollToAbout = () => {
    const element = document.getElementById('about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section className="hero-section">
      <div className="particles-container">
        {particles.map((particle) => (
          <motion.div
            key={particle.id}
            className={`particle ${theme === 'dark' ? 'star' : 'dot'}`}
            style={{
              left: particle.x,
              top: particle.y,
              width: particle.size,
              height: particle.size,
            }}
            animate={{
              x: (mousePosition.x - window.innerWidth / 2) * particle.speed * 0.5,
              y: (mousePosition.y - window.innerHeight / 2) * particle.speed * 0.5,
            }}
            transition={{
              type: "spring",
              stiffness: 50,
              damping: 15,
            }}
          />
        ))}
      </div>

      <div className="hero-content">
        <motion.h1
          className="hero-name"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Nyan Aung
        </motion.h1>

        <motion.div
          className="hero-subtitle"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <TypewriterText 
            texts={[
              "Full-Stack Engineer",
              "Software Engineer",
              "Backend Engineer",
              "Frontend Engineer"
            ]}
            speed={80}
            deleteSpeed={40}
            pauseTime={2000}
          />
        </motion.div>
      </div>

      <motion.div
        className="scroll-indicator"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.5 }}
        onClick={scrollToAbout}
      >
        <motion.div
          className="scroll-arrow"
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          ↓
        </motion.div>
      </motion.div>

      <style jsx>{`
        .hero-section {
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          position: relative;
          background: linear-gradient(135deg, var(--gradient-start) 0%, var(--gradient-end) 100%);
          overflow: hidden;
        }

        .particles-container {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          pointer-events: none;
        }

        .particle {
          position: absolute;
          opacity: 0.6;
          pointer-events: none;
        }

        .particle.star {
          background: var(--accent-blue);
          border-radius: 50%;
          box-shadow: 0 0 8px rgba(37, 99, 235, 0.4);
          animation: pulse 2s ease-in-out infinite alternate;
        }

        .particle.dot {
          background: var(--accent-blue);
          border-radius: 50%;
          box-shadow: 0 0 8px rgba(37, 99, 235, 0.4);
          animation: pulse 2s ease-in-out infinite alternate;
        }

        @keyframes pulse {
          0% { opacity: 0.6; transform: scale(0.9); }
          100% { opacity: 1; transform: scale(1.2); }
        }

        .hero-content {
          text-align: center;
          z-index: 10;
          position: relative;
        }

        .hero-name {
          font-family: 'Poppins', sans-serif;
          font-size: 80px;
          font-weight: 700;
          color: var(--text-light);
          margin-bottom: 20px;
          background: linear-gradient(45deg, var(--text-light), var(--accent-blue));
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .hero-subtitle {
          font-family: 'Inter', sans-serif;
          font-size: 24px;
          font-weight: 300;
          color: var(--text-light);
          opacity: 0.8;
          max-width: 600px;
          margin: 0 auto;
          min-height: 30px;
        }

        .typewriter-text {
          display: inline-block;
        }

        .cursor {
          animation: blink 1s infinite;
          color: var(--accent-blue);
          font-weight: 400;
        }

        @keyframes blink {
          0%, 50% { opacity: 1; }
          51%, 100% { opacity: 0; }
        }

        .scroll-indicator {
          position: absolute;
          bottom: 30px;
          left: 50%;
          transform: translateX(-50%);
          cursor: pointer;
          color: var(--text-light);
          opacity: 0.7;
        }

        .scroll-arrow {
          font-size: 24px;
          font-weight: 300;
        }

        @media (max-width: 768px) {
          .hero-name {
            font-size: 50px;
          }

          .hero-subtitle {
            font-size: 18px;
            padding: 0 20px;
          }

          .scroll-indicator {
            bottom: 20px;
          }

          .scroll-arrow {
            font-size: 20px;
          }
        }

        @media (max-width: 480px) {
          .hero-name {
            font-size: 40px;
          }

          .hero-subtitle {
            font-size: 16px;
          }
        }
      `}</style>
    </section>
  );
};

export default Hero;
