import React from 'react';
import { motion } from 'framer-motion';

const Skills = ({ theme }) => {
  const languages = [
    'Python', 'JavaScript', 'TypeScript', 'Java', 'C++', 'C', 'HTML', 'CSS', 'SQL'
  ];
  const frameworks = [
    'React', 'Node.js', 'Express', 'Django', 'scikit-learn', 'NumPy', 'Mongoose'
  ];
  const tools = [
    'Git', 'GitHub', 'Docker', 'MongoDB', 'PostgreSQL', 'JWT', 'Cloudinary', 'REST APIs', 'Postman', 'Vercel', 'Railway'
  ];

  const getIconPath = (name) => {
    const normalized = name.toLowerCase()
      .replace('+', 'p')
      .replace(/\s+/g, '-')
      .replace(/\./g, '')
      .replace(/#/g, 'sharp');

    switch (name) {
      case 'C++': return '/icons/cpp.svg';
      case 'C': return '/icons/c.svg';
      case 'C#': return '/icons/csharp.svg';
      case 'Node.js': return '/icons/nodejs.svg';
      case 'scikit-learn': return '/icons/scikit-learn.svg';
      case 'REST APIs': return '/icons/rest.svg';
      case 'GitHub': return '/icons/github.svg';
      case 'PostgreSQL': return '/icons/postgresql.svg';
      default: return `/icons/${normalized}.svg`;
    }
  };

  return (
    <section id="skills" className="skills-section">
      <motion.div 
        className="skills-container"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8, ease: 'easeOut' }}
        viewport={{ once: false, margin: '-100px' }}
