import React from 'react';
import { motion } from 'framer-motion';

const SplashScreen = () => {
  return (
    <motion.div
      className="splash-screen"
      initial={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      transition={{ duration: 0.8 }}
    >
      <div className="splash-content">
        <motion.div
          className="initials-container"
          initial={{ scale: 0, rotate: -180 }}
          animate={{ scale: 1, rotate: 0 }}
          transition={{ 
            duration: 1.5, 
            ease: "easeOut",
            delay: 0.5 
          }}
        >
          <div className="initials">NA</div>
        </motion.div>

        <motion.div
          className="loading-text"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ 
            duration: 0.8, 
            delay: 1.5 
          }}
        >
          Loading Portfolio...
        </motion.div>

        <motion.div
          className="loading-bar"
          initial={{ width: 0 }}
          animate={{ width: "100%" }}
          transition={{ 
            duration: 2, 
            delay: 2,
            ease: "easeInOut"
          }}
        />
      </div>

      <style jsx>{`
        .splash-screen {
          position: fixed;
          top: 0;
          left: 0;
          width: 100vw;
          height: 100vh;
          background: #000000;
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 9999;
        }

        .splash-content {
          text-align: center;
          color: var(--text-light);
        }

        .initials-container {
          margin-bottom: 40px;
        }

        .initials {
          font-family: 'Poppins', sans-serif;
          font-size: 120px;
          font-weight: 700;
          background: linear-gradient(45deg, var(--accent-blue), #60a5fa);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
          letter-spacing: 8px;
        }

        .loading-text {
          font-family: 'Inter', sans-serif;
          font-size: 18px;
          font-weight: 300;
          margin-bottom: 30px;
          opacity: 0.8;
        }

        .loading-bar {
          width: 200px;
          height: 2px;
          background: linear-gradient(90deg, transparent, var(--accent-blue), transparent);
          margin: 0 auto;
          border-radius: 1px;
        }

        @media (max-width: 768px) {
          .initials {
            font-size: 80px;
            letter-spacing: 4px;
          }

          .loading-text {
            font-size: 16px;
          }

          .loading-bar {
            width: 150px;
          }
        }
      `}</style>
    </motion.div>
  );
};

export default SplashScreen;
