import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import TypewriterText from './TypewriterText';

const Hero = ({ theme }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  const scrollToAbout = () => {
    const element = document.getElementById('about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section className="hero-section">
      <motion.div 
        className="cursor-follow-bg"
        animate={{
          x: mousePosition.x - 200,
          y: mousePosition.y - 200,
        }}
        transition={{
          type: "spring",
          stiffness: 50,
          damping: 15,
        }}
      />
      <div className="hero-content">
        <motion.h1
          className="hero-name"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Nyan Aung
        </motion.h1>

        <motion.div
          className="hero-subtitle"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <TypewriterText 
            texts={[
              "Full-Stack Engineer",
              "Software Engineer",
              "Backend Engineer",
              "Frontend Engineer"
            ]}
            speed={80}
            deleteSpeed={40}
            pauseTime={2000}
          />
        </motion.div>
      </div>

      <motion.div
        className="scroll-indicator"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.5 }}
        onClick={scrollToAbout}
      >
        <motion.div
          className="scroll-arrow"
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          â†“
        </motion.div>
      </motion.div>

      <style jsx>{`
        .hero-section {
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          position: relative;
          background: linear-gradient(135deg, var(--gradient-start) 0%, var(--gradient-end) 100%);
          overflow: hidden;
        }

        .cursor-follow-bg {
          position: absolute;
          width: 400px;
          height: 400px;
          background: radial-gradient(circle, rgba(37, 99, 235, 0.1) 0%, rgba(37, 99, 235, 0.05) 50%, transparent 70%);
          border-radius: 50%;
          pointer-events: none;
          z-index: 1;
          filter: blur(40px);
        }

        .hero-content {
          text-align: center;
          z-index: 10;
          position: relative;
        }

        .hero-name {
          font-family: 'Poppins', sans-serif;
          font-size: 80px;
          font-weight: 700;
          color: var(--text-light);
          margin-bottom: 20px;
          background: linear-gradient(45deg, var(--text-light), var(--accent-blue));
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .hero-subtitle {
          font-family: 'Inter', sans-serif;
          font-size: 24px;
          font-weight: 300;
          color: var(--text-light);
          opacity: 0.8;
          max-width: 600px;
          margin: 0 auto;
          min-height: 30px;
        }

        .typewriter-text {
          display: inline-block;
        }

        .cursor {
          animation: blink 1s infinite;
          color: var(--accent-blue);
          font-weight: 400;
        }

        @keyframes blink {
          0%, 50% { opacity: 1; }
          51%, 100% { opacity: 0; }
        }

        .scroll-indicator {
          position: absolute;
          bottom: 30px;
          left: 50%;
          transform: translateX(-50%);
          cursor: pointer;
          color: var(--text-light);
          opacity: 0.7;
        }

        .scroll-arrow {
          font-size: 24px;
          font-weight: 300;
        }

        @media (max-width: 768px) {
          .hero-name {
            font-size: 50px;
          }

          .hero-subtitle {
            font-size: 18px;
            padding: 0 20px;
          }

          .scroll-indicator {
            bottom: 20px;
          }

          .scroll-arrow {
            font-size: 20px;
          }
        }

        @media (max-width: 480px) {
          .hero-name {
            font-size: 40px;
          }

          .hero-subtitle {
            font-size: 16px;
          }
        }
      `}</style>
    </section>
  );
};

export default Hero;
