import React from 'react';
import { motion } from 'framer-motion';

const Navbar = ({ theme, toggleTheme }) => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const scrollToTop = () => {
    console.log('Scrolling to top...'); // Debug log
    try {
      // Try to disable scroll snap on multiple elements
      const body = document.body;
      const html = document.documentElement;
      const root = document.getElementById('root');

      // Store original values
      const originalBodySnap = body.style.scrollSnapType;
      const originalHtmlSnap = html.style.scrollSnapType;
      const originalRootSnap = root ? root.style.scrollSnapType : '';

      // Disable scroll snap
      body.style.scrollSnapType = 'none';
      html.style.scrollSnapType = 'none';
      if (root) root.style.scrollSnapType = 'none';

      console.log('Scroll snap disabled');

      // Force immediate scroll to top
      window.scrollTo(0, 0);
      document.documentElement.scrollTop = 0;
      document.body.scrollTop = 0;

      // Try smooth scroll after immediate scroll
      setTimeout(() => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      }, 10);

      // Re-enable scroll snap after delay
      setTimeout(() => {
        body.style.scrollSnapType = originalBodySnap;
        html.style.scrollSnapType = originalHtmlSnap;
        if (root) root.style.scrollSnapType = originalRootSnap;
        console.log('Scroll snap re-enabled');
      }, 1000);

      console.log('Scroll commands executed');
    } catch (error) {
      console.error('Error scrolling to top:', error);
    }
  };

