import React from 'react';
import { motion } from 'framer-motion';
import { FaSun, FaMoon } from 'react-icons/fa';

const Navbar = ({ theme, toggleTheme }) => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <motion.nav
      className="navbar"
      initial={{ opacity: 0, y: -50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.3 }}
    >
      <div className="nav-container">
        <motion.div
          className="nav-logo"
          whileHover={{ scale: 1.05 }}
          onClick={scrollToTop}
        >
          Nyan Aung
        </motion.div>

        <div className="nav-links">
          <button onClick={() => scrollToSection('about')}>About</button>
          <button onClick={() => scrollToSection('projects')}>Projects</button>
          <button onClick={() => scrollToSection('contact')}>Contact</button>
        </div>

        <motion.button
          className="theme-toggle"
          onClick={toggleTheme}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
        >
          {theme === 'dark' ? <FaSun /> : <FaMoon />}
        </motion.button>
      </div>

      <style jsx>{`
        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          background: ${theme === 'dark' ? 'rgba(36, 36, 36, 0.9)' : 'rgba(238, 238, 238, 0.9)'};
          backdrop-filter: blur(10px);
          z-index: 100;
          padding: 20px 0;
        }

        .nav-container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .nav-logo {
          font-family: 'Poppins', sans-serif;
          font-size: 22px;
          font-weight: 700;
          color: ${theme === 'dark' ? '#10b981' : '#333333'};
          cursor: pointer;
          letter-spacing: 0.5px;
        }

        .nav-links {
          display: flex;
          gap: 30px;
          position: absolute;
          left: 50%;
          transform: translateX(-50%);
        }

        .nav-links button {
          background: none;
          border: none;
          color: ${theme === 'dark' ? '#10b981' : '#333333'};
          font-family: 'Inter', sans-serif;
          font-size: 16px;
          font-weight: 500;
          cursor: pointer;
          transition: color 0.3s ease;
          position: relative;
        }

        .nav-links button:hover {
          color: ${theme === 'dark' ? '#10b981' : '#333333'};
          opacity: 0.7;
        }

        .nav-links button::after {
          content: '';
          position: absolute;
          bottom: -5px;
          left: 0;
          width: 0;
          height: 2px;
          background: ${theme === 'dark' ? '#10b981' : '#333333'};
          transition: width 0.3s ease;
        }

        .nav-links button:hover::after {
          width: 100%;
        }

        .theme-toggle {
          background: none;
          border: none;
          color: ${theme === 'dark' ? '#10b981' : '#333333'};
          font-size: 20px;
          cursor: pointer;
          padding: 9px;
          margin-right: 300px;
          border-radius: 50%;
          transition: all 0.3s ease;
          display: flex;
          align-items: center;
          justify-content: center;
          width: 40px;
          height: 40px;
        }

        .theme-toggle:hover {
          background: ${theme === 'dark' ? 'rgba(16, 185, 129, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
          color: ${theme === 'dark' ? '#10b981' : '#000000'};
        }

        @media (max-width: 768px) {
          .navbar {
            padding: 15px 0;
          }

          .nav-container {
            padding: 0 15px;
          }

          .nav-logo {
            font-size: 18px;
          }

          .nav-links {
            gap: 20px;
          }

          .nav-links button {
            font-size: 14px;
          }

          .theme-toggle {
            font-size: 18px;
            width: 36px;
            height: 36px;
          }
        }
      `}</style>
    </motion.nav>
  );
};

export default Navbar;
