            0 25px 50px ${theme === 'dark' ? 'rgba(0, 0, 0, 0.4)' : 'rgba(0, 0, 0, 0.15)'};
          transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
          background: ${theme === 'dark' ? 'linear-gradient(135deg, rgba(227, 229, 230, 0.05), rgba(227, 229, 230, 0.02))' : 'linear-gradient(135deg, rgba(0, 0, 0, 0.05), rgba(0, 0, 0, 0.02))'};
        }

        .image-container::after {
          content: '';
          position: absolute;
          top: -10px;
          left: -10px;
          right: -10px;
          bottom: -10px;
          background: conic-gradient(from 0deg, ${theme === 'dark' ? '#e3e5e6' : '#000000'}, transparent, ${theme === 'dark' ? '#e3e5e6' : '#000000'}, transparent, ${theme === 'dark' ? '#e3e5e6' : '#000000'});
          border-radius: 40px;
          z-index: -1;
          opacity: 0;
          transition: opacity 0.4s ease;
          animation: rotate 8s linear infinite;
        }

        @keyframes rotate {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        .image-container::before {
          content: '';
          position: absolute;
          top: -2px;
          left: -2px;
          right: -2px;
          bottom: -2px;
          background: ${theme === 'dark' ? 'linear-gradient(45deg, #e3e5e6, #ffffff, #e3e5e6, #ffffff)' : 'linear-gradient(45deg, #000000, #374151, #000000, #374151)'};
          border-radius: 32px;
          z-index: -1;
          opacity: 0;
          transition: opacity 0.3s ease;
        }

        .image-container:hover {
          transform: translateY(-8px) scale(1.02);
          box-shadow: 
            0 35px 70px ${theme === 'dark' ? 'rgba(0, 0, 0, 0.5)' : 'rgba(0, 0, 0, 0.2)'},
            0 0 0 1px ${theme === 'dark' ? 'rgba(227, 229, 230, 0.2)' : 'rgba(0, 0, 0, 0.1)'};
        }

        .image-container:hover::before {
          opacity: 0.3;
        }

        .image-container:hover::after {
          opacity: 0.6;
        }

        .profile-image {
          width: 100%;
          height: 100%;
          object-fit: cover;
          object-position: center;
          transition: transform 0.4s ease;
        }

        .image-container:hover .profile-image {
          transform: scale(1.05);
        }

        .skills-wrapper {
          margin-top: 60px;
        }

        .skills-grid {
          display: grid;
          grid-template-columns: repeat(2, minmax(0, 1fr));
          gap: 40px;
          width: 100%;
        }

        .skills-category {
          background: ${theme === 'dark' ? 'rgba(227, 229, 230, 0.04)' : 'rgba(0, 0, 0, 0.04)'};
          border: 1px solid ${theme === 'dark' ? 'rgba(227, 229, 230, 0.15)' : 'rgba(0, 0, 0, 0.08)'};
          border-radius: 16px;
          padding: 24px;
          box-shadow: ${theme === 'dark' ? '0 10px 30px rgba(0,0,0,0.35)' : '0 10px 20px rgba(0,0,0,0.08)'};
        }

        .skills-category-title {
          font-family: 'Poppins', sans-serif;
          font-size: 20px;
          font-weight: 700;
          margin-bottom: 16px;
          color: ${theme === 'dark' ? '#e3e5e6' : '#000000'};
        }

        .skills-items {
          display: grid;
          grid-template-columns: repeat(7, 1fr);
          gap: 16px;
        }

        .skill {
          height: 60px;
          width: 60px;
          display: inline-flex;
          align-items: center;
          justify-content: center;
          border-radius: 14px;
          background: ${theme === 'dark' ? 'rgba(227, 229, 230, 0.06)' : 'rgba(0, 0, 0, 0.06)'};
          border: 1px solid ${theme === 'dark' ? 'rgba(227, 229, 230, 0.18)' : 'rgba(0, 0, 0, 0.08)'};
          color: ${theme === 'dark' ? '#e3e5e6' : '#000000'};
          position: relative;
          transition: transform 0.25s ease, box-shadow 0.25s ease, background 0.25s ease;
          box-shadow: ${theme === 'dark' ? '0 6px 16px rgba(0,0,0,0.35)' : '0 6px 12px rgba(0,0,0,0.08)'};
        }

        .skill:hover {
          transform: translateY(-6px) rotate(-3deg);
          background: ${theme === 'dark' ? 'rgba(227, 229, 230, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
          box-shadow: ${theme === 'dark' ? '0 10px 24px rgba(0,0,0,0.45), 0 0 12px rgba(227,229,230,0.25) inset' : '0 10px 20px rgba(0,0,0,0.15)'};
        }

        .skill :global(svg) {
          width: 28px;
          height: 28px;
        }

        .skill::after {
          content: attr(data-tip);
          position: absolute;
          bottom: -32px;
          left: 50%;
          transform: translateX(-50%);
          white-space: nowrap;
          background: ${theme === 'dark' ? 'rgba(0, 0, 0, 0.6)' : 'rgba(0, 0, 0, 0.8)'};
          color: #ffffff;
          padding: 4px 8px;
          border-radius: 6px;
          font-size: 11px;
          opacity: 0;
          pointer-events: none;
          transition: opacity 0.2s ease, transform 0.2s ease;
        }

        .skill:hover::after {
          opacity: 1;
          transform: translateX(-50%) translateY(-2px);
        }

