import React, { useMemo, useEffect, useRef } from 'react';
import './Projects.css';

const Projects = ({ theme }) => {
  const wrapperRef = useRef(null);
  const allWindowsRef = useRef([]);

  const sectionStyles = useMemo(() => ({
    '--paralaxY': '50%',
    '--projects-bg': theme === 'dark' ? 'linear-gradient(135deg, #042a1c 0%, #042a1c 100%)' : 'linear-gradient(135deg, #EEEEEE 0%, #EEEEEE 100%)',
    '--projects-title-color': theme === 'dark' ? '#e3e5e6' : '#000000',
  }), [theme]);

  const projects = useMemo(() => [
    { id: 1, title: 'E-Commerce Platform', image: 'https://picsum.photos/id/101/1920/1080' },
    { id: 2, title: 'Portfolio Website', image: 'https://picsum.photos/id/102/1920/1080' },
    { id: 3, title: 'Blog System', image: 'https://picsum.photos/id/103/1920/1080' },
    { id: 4, title: 'Dashboard App', image: 'https://picsum.photos/id/104/1920/1080' },
    { id: 5, title: 'Mobile App', image: 'https://picsum.photos/id/105/1920/1080' },
    { id: 6, title: 'Web Application', image: 'https://picsum.photos/id/106/1920/1080' },
    { id: 7, title: 'API Service', image: 'https://picsum.photos/id/107/1920/1080' },
    { id: 8, title: 'Data Visualization', image: 'https://picsum.photos/id/108/1920/1080' },
    { id: 9, title: 'Game Development', image: 'https://picsum.photos/id/109/1920/1080' },
    { id: 10, title: 'AI Project', image: 'https://picsum.photos/id/110/1920/1080' },
    { id: 11, title: 'Blockchain App', image: 'https://picsum.photos/id/111/1920/1080' },
    { id: 12, title: 'IoT Platform', image: 'https://picsum.photos/id/112/1920/1080' },
    { id: 13, title: 'Cloud Service', image: 'https://picsum.photos/id/113/1920/1080' },
    { id: 14, title: 'Security Tool', image: 'https://picsum.photos/id/114/1920/1080' },
    { id: 15, title: 'Analytics Dashboard', image: 'https://picsum.photos/id/115/1920/1080' },
    { id: 16, title: 'Social Media App', image: 'https://picsum.photos/id/116/1920/1080' },
    { id: 17, title: 'E-Learning Platform', image: 'https://picsum.photos/id/117/1920/1080' },
    { id: 18, title: 'Healthcare App', image: 'https://picsum.photos/id/118/1920/1080' },
    { id: 19, title: 'Finance Tracker', image: 'https://picsum.photos/id/119/1920/1080' },
    { id: 20, title: 'Travel Planner', image: 'https://picsum.photos/id/120/1920/1080' },
    { id: 21, title: 'Fitness App', image: 'https://picsum.photos/id/121/1920/1080' },
    { id: 22, title: 'Music Player', image: 'https://picsum.photos/id/122/1920/1080' },
    { id: 23, title: 'Photo Editor', image: 'https://picsum.photos/id/123/1920/1080' },
    { id: 24, title: 'Video Streaming', image: 'https://picsum.photos/id/124/1920/1080' },
    { id: 25, title: 'Chat Application', image: 'https://picsum.photos/id/125/1920/1080' },
    { id: 26, title: 'Task Manager', image: 'https://picsum.photos/id/126/1920/1080' },
    { id: 27, title: 'Weather App', image: 'https://picsum.photos/id/127/1920/1080' },
    { id: 28, title: 'News Reader', image: 'https://picsum.photos/id/128/1920/1080' },
    { id: 29, title: 'Recipe App', image: 'https://picsum.photos/id/129/1920/1080' },
    { id: 30, title: 'Shopping List', image: 'https://picsum.photos/id/130/1920/1080' }
  ], []);

  useEffect(() => {
    const wrapper = wrapperRef.current;
    const allWindows = allWindowsRef.current;

    const handleWheel = (e) => {
      if (e.deltaX !== 0) return;
      e.preventDefault();
      wrapper.scrollLeft += 10 * e.deltaY;
    };

    const calculateParalax = (container, elem) => {
      const elemCenterX = elem.offsetLeft + (elem.offsetWidth / 2);
      const viewCenterX = container.scrollLeft + (container.offsetWidth / 2);
      const delta = elemCenterX - viewCenterX;
      const max = container.offsetWidth / 2 + elem.offsetWidth / 2;
      elem.style.setProperty('--paralaxX', (delta * 100 / max) + 50 + "%");
    };

    const handleScroll = (e) => {
      for (let i = 0; i < allWindows.length; i++) {
        calculateParalax(e.target, allWindows[i]);
      }
    };

    const handleMouseMove = (e) => {
      const delta = e.clientY - (document.documentElement.offsetHeight / 2);
      const max = document.documentElement.offsetHeight * 2;
      document.documentElement.style.setProperty('--paralaxY', (delta * 100 / max) + 50 + "%");
    };

    const handleClick = (e) => {
      if (e.target.classList.contains('selected')) {
        e.target.classList.remove('selected');
        return;
      }
      for (let i = 0; i < allWindows.length; i++) {
        allWindows[i].classList.remove('selected');
      }
      e.target.classList.add('selected');
    };

    // Add event listeners
    wrapper.addEventListener('wheel', handleWheel);
    wrapper.addEventListener('scroll', handleScroll);
    document.documentElement.addEventListener('mousemove', handleMouseMove);

    for (let i = 0; i < allWindows.length; i++) {
      allWindows[i].addEventListener('click', handleClick);
    }

    // Center the scroll position
    wrapper.scrollLeft = wrapper.scrollWidth / 2 - wrapper.clientWidth / 2;

    // Cleanup
    return () => {
      wrapper.removeEventListener('wheel', handleWheel);
      wrapper.removeEventListener('scroll', handleScroll);
      document.documentElement.removeEventListener('mousemove', handleMouseMove);
      for (let i = 0; i < allWindows.length; i++) {
        allWindows[i].removeEventListener('click', handleClick);
      }
    };
  }, []);

  return (
    <section 
      id="projects" 
      className="projects-section"
      style={sectionStyles}
    >
      <div className="projects-container">
        <div className="projects-content">
          <div className="projects-header">
            <h2 className="projects-title">
              Projects
            </h2>
          </div>

          <div id="wrapper" ref={wrapperRef}>
          {projects.map((project, index) => (
              <div
              key={project.id}
                className="window"
                ref={el => allWindowsRef.current[index] = el}
                style={{
                  '--paralaxX': '50%',
                  backgroundImage: `url(${project.image})`
                }}
              >
                <div className="window-content">
                <h3 className="project-title">{project.title}</h3>
                </div>
              </div>
          ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
