import React, { useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import TypewriterText from './TypewriterText';
import { FaLinkedin, FaGithub, FaEnvelope, FaDownload } from 'react-icons/fa';

const Hero = ({ theme }) => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const particlesRef = useRef([]);
  const mouseRef = useRef({ x: 0, y: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;

      // Dynamically adjust particle count based on new screen size
      adjustParticleCount();
    };

    const adjustParticleCount = () => {
      const currentParticleCount = particlesRef.current.length;
      let targetParticleCount;

      if (window.innerWidth <= 375) {
        targetParticleCount = 40; // iPhone SE
      } else if (window.innerWidth <= 480) {
        targetParticleCount = 60; // iPhone 12 Pro
      } else if (window.innerWidth <= 768) {
        targetParticleCount = 80; // iPad Mini
      } else if (window.innerWidth <= 1024) {
        targetParticleCount = 100; // iPad Air
      } else if (window.innerWidth <= 1366) {
        targetParticleCount = 120; // iPad Pro
      } else {
        targetParticleCount = 150; // Desktop
      }

      // If we need more particles, add them
      if (targetParticleCount > currentParticleCount) {
        for (let i = currentParticleCount; i < targetParticleCount; i++) {
          particlesRef.current.push({
            x: Math.random() * canvas.width,
            y: Math.random() * canvas.height,
            vx: (Math.random() - 0.5) * 1.2, // Increased speed from 0.6 to 1.2
            vy: (Math.random() - 0.5) * 1.2, // Increased speed from 0.6 to 1.2
            size: Math.random() * 4 + 1,
            opacity: Math.random() * 0.6 + 0.2,
            originalSize: Math.random() * 4 + 1,
            pulseSpeed: Math.random() * 0.02 + 0.01
          });
        }
      }
      // If we need fewer particles, remove them
      else if (targetParticleCount < currentParticleCount) {
        particlesRef.current = particlesRef.current.slice(0, targetParticleCount);
      }
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Initialize particles
    const initParticles = () => {
      particlesRef.current = [];

      // Dynamic particle count based on screen size
      let particleCount;
      if (window.innerWidth <= 375) {
        particleCount = 40; // iPhone SE
      } else if (window.innerWidth <= 480) {
        particleCount = 60; // iPhone 12 Pro
      } else if (window.innerWidth <= 768) {
        particleCount = 80; // iPad Mini
      } else if (window.innerWidth <= 1024) {
        particleCount = 100; // iPad Air
      } else if (window.innerWidth <= 1366) {
        particleCount = 120; // iPad Pro
      } else {
        particleCount = 150; // Desktop
      }

      for (let i = 0; i < particleCount; i++) {
        particlesRef.current.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          vx: (Math.random() - 0.5) * 0.6,
          vy: (Math.random() - 0.5) * 0.6,
          size: Math.random() * 4 + 1,
          opacity: Math.random() * 0.6 + 0.2,
          originalSize: Math.random() * 4 + 1,
          pulseSpeed: Math.random() * 0.02 + 0.01
        });
      }
    };

    initParticles();

    // Mouse move handler
    const handleMouseMove = (e) => {
      mouseRef.current.x = e.clientX;
      mouseRef.current.y = e.clientY;
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Animation loop
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Update and draw particles
      particlesRef.current.forEach((particle, index) => {
        // Update position
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Bounce off edges with faster response
        if (particle.x < 0 || particle.x > canvas.width) {
          particle.vx *= -1;
          particle.x = Math.max(0, Math.min(canvas.width, particle.x));
        }
        if (particle.y < 0 || particle.y > canvas.height) {
          particle.vy *= -1;
          particle.y = Math.max(0, Math.min(canvas.height, particle.y));
        }

        // Mouse interaction - cursor acts as a particle that attracts others
        const dx = mouseRef.current.x - particle.x;
        const dy = mouseRef.current.y - particle.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 150) {
          const force = (150 - distance) / 150;
          // Increased attraction force for faster response
          particle.x += dx * force * 0.02;
          particle.y += dy * force * 0.02;

          // Subtle size increase when near cursor
          particle.size = particle.originalSize + force * 1;
          particle.opacity = Math.min(0.8, particle.opacity + force * 0.1);
        } else {
          // Reset size and opacity when away from cursor
          particle.size = particle.originalSize + Math.sin(Date.now() * particle.pulseSpeed) * 0.5;
          particle.opacity = Math.random() * 0.6 + 0.2;
        }

        // Draw particle with glow effect
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);

        // Add glow effect
        ctx.shadowBlur = particle.size * 2;
        ctx.shadowColor = theme === 'dark' ? 'rgba(16, 185, 129, 0.5)' : 'rgba(51, 51, 51, 0.3)';

        ctx.fillStyle = theme === 'dark' ? `rgba(16, 185, 129, ${particle.opacity})` : `rgba(51, 51, 51, ${particle.opacity})`;
        ctx.fill();

        // Reset shadow
        ctx.shadowBlur = 0;
      });

      // Draw connections with improved logic
      const connections = [];

      // First pass: collect all valid connections
      particlesRef.current.forEach((particle, i) => {
        particlesRef.current.forEach((otherParticle, j) => {
          if (i < j) { // Avoid duplicate connections and self-connections
            const dx = particle.x - otherParticle.x;
            const dy = particle.y - otherParticle.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            if (distance < 120) { // Increased connection distance
              connections.push({
                particle1: particle,
                particle2: otherParticle,
                distance: distance,
                opacity: Math.max(0.1, (120 - distance) / 120 * 0.5)
              });
            }
          }
        });
      });

      // Second pass: draw connections with enhanced effects
      connections.forEach(connection => {
        const { particle1, particle2, opacity } = connection;

        // Check if either particle is near cursor for enhanced effect
        const mouseDx1 = particle1.x - mouseRef.current.x;
        const mouseDy1 = particle1.y - mouseRef.current.y;
        const mouseDx2 = particle2.x - mouseRef.current.x;
        const mouseDy2 = particle2.y - mouseRef.current.y;
        const mouseDistance1 = Math.sqrt(mouseDx1 * mouseDx1 + mouseDy1 * mouseDy1);
        const mouseDistance2 = Math.sqrt(mouseDx2 * mouseDx2 + mouseDy2 * mouseDy2);

        let enhancedOpacity = opacity;
        let lineWidth = 1;
        let hasGlow = false;

        // Enhanced effects when near cursor
        if (mouseDistance1 < 150 || mouseDistance2 < 150) {
          enhancedOpacity = Math.min(0.9, opacity + 0.4);
          lineWidth = 2;
          hasGlow = true;
        }

        // Draw the connection line
        ctx.beginPath();
        ctx.moveTo(particle1.x, particle1.y);
        ctx.lineTo(particle2.x, particle2.y);

        // Add glow effect if near cursor
        if (hasGlow) {
          ctx.shadowBlur = 4;
          ctx.shadowColor = theme === 'dark' ? 'rgba(16, 185, 129, 0.4)' : 'rgba(51, 51, 51, 0.3)';
        }

        ctx.strokeStyle = theme === 'dark' ? `rgba(16, 185, 129, ${enhancedOpacity})` : `rgba(51, 51, 51, ${enhancedOpacity})`;
        ctx.lineWidth = lineWidth;
        ctx.stroke();

        // Reset shadow
        ctx.shadowBlur = 0;
      });

      // Draw cursor particle (invisible but acts as connection hub)
      const cursorParticle = {
        x: mouseRef.current.x,
        y: mouseRef.current.y,
        size: 8
      };

      // Draw connections from cursor to nearby particles
      particlesRef.current.forEach(particle => {
        const dx = cursorParticle.x - particle.x;
        const dy = cursorParticle.y - particle.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 150) {
          const opacity = (150 - distance) / 150 * 0.6;

          ctx.beginPath();
          ctx.moveTo(cursorParticle.x, cursorParticle.y);
          ctx.lineTo(particle.x, particle.y);

          // Strong glow for cursor connections
          ctx.shadowBlur = 6;
          ctx.shadowColor = theme === 'dark' ? 'rgba(16, 185, 129, 0.6)' : 'rgba(51, 51, 51, 0.4)';

          ctx.strokeStyle = theme === 'dark' ? `rgba(16, 185, 129, ${opacity})` : `rgba(51, 51, 51, ${opacity})`;
          ctx.lineWidth = 2;
          ctx.stroke();

          // Reset shadow
          ctx.shadowBlur = 0;
        }
      });

      // Draw cursor particle (optional - can be made invisible)
      ctx.beginPath();
      ctx.arc(cursorParticle.x, cursorParticle.y, cursorParticle.size, 0, Math.PI * 2);
      ctx.shadowBlur = 10;
      ctx.shadowColor = theme === 'dark' ? 'rgba(16, 185, 129, 0.8)' : 'rgba(51, 51, 51, 0.5)';
      ctx.fillStyle = theme === 'dark' ? 'rgba(16, 185, 129, 0.3)' : 'rgba(51, 51, 51, 0.2)';
      ctx.fill();
      ctx.shadowBlur = 0;

      animationRef.current = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      window.removeEventListener('mousemove', handleMouseMove);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [theme]);

