        }
    }
    let randomPoint = null;
    for (let attempts = 0; attempts < maxAttempts; attempts++) {
        const tmpPoint = {
            x: min.x + getRandom() * (max.x - min.x),
            y: min.y + getRandom() * (max.y - min.y),
