import React, { useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import TypewriterText from './TypewriterText';

const Hero = ({ theme }) => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const particlesRef = useRef([]);
  const mouseRef = useRef({ x: 0, y: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Initialize particles
    const initParticles = () => {
      particlesRef.current = [];
      const particleCount = 200;

      for (let i = 0; i < particleCount; i++) {
        particlesRef.current.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          vx: (Math.random() - 0.5) * 0.8,
          vy: (Math.random() - 0.5) * 0.8,
          size: Math.random() * 4 + 1,
          opacity: Math.random() * 0.6 + 0.2,
          originalSize: Math.random() * 4 + 1,
          pulseSpeed: Math.random() * 0.02 + 0.01
        });
      }
    };

    initParticles();

    // Mouse move handler
    const handleMouseMove = (e) => {
      mouseRef.current.x = e.clientX;
      mouseRef.current.y = e.clientY;
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Animation loop
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Update and draw particles
      particlesRef.current.forEach((particle, index) => {
        // Update position
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Bounce off edges
        if (particle.x < 0 || particle.x > canvas.width) particle.vx *= -1;
        if (particle.y < 0 || particle.y > canvas.height) particle.vy *= -1;

        // Keep particles in bounds
        particle.x = Math.max(0, Math.min(canvas.width, particle.x));
        particle.y = Math.max(0, Math.min(canvas.height, particle.y));

        // Mouse interaction - stronger attraction
        const dx = particle.x - mouseRef.current.x;
        const dy = particle.y - mouseRef.current.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 150) {
          const force = (150 - distance) / 150;
          particle.x += dx * force * 0.05;
          particle.y += dy * force * 0.05;

          // Make particles grow when near cursor
          particle.size = particle.originalSize + force * 3;
          particle.opacity = Math.min(1, particle.opacity + force * 0.3);
        } else {
          // Reset size and opacity when away from cursor
          particle.size = particle.originalSize + Math.sin(Date.now() * particle.pulseSpeed) * 0.5;
          particle.opacity = Math.random() * 0.6 + 0.2;
        }

        // Draw particle with glow effect
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);

        // Add glow effect
        ctx.shadowBlur = particle.size * 2;
        ctx.shadowColor = theme === 'dark' ? 'rgba(255, 255, 255, 0.5)' : 'rgba(37, 99, 235, 0.5)';

        ctx.fillStyle = theme === 'dark' ? `rgba(255, 255, 255, ${particle.opacity})` : `rgba(37, 99, 235, ${particle.opacity})`;
        ctx.fill();

        // Reset shadow
        ctx.shadowBlur = 0;
      });

      // Draw connections with optimized algorithm
      const connections = [];

      // First pass: collect all connections to avoid duplicate drawing
      for (let i = 0; i < particlesRef.current.length; i++) {
        for (let j = i + 1; j < particlesRef.current.length; j++) {
          const particle = particlesRef.current[i];
          const otherParticle = particlesRef.current[j];

          const dx = particle.x - otherParticle.x;
          const dy = particle.y - otherParticle.y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < 120) {
            // Calculate mouse influence for both particles
            const mouseDx1 = particle.x - mouseRef.current.x;
            const mouseDy1 = particle.y - mouseRef.current.y;
            const mouseDx2 = otherParticle.x - mouseRef.current.x;
            const mouseDy2 = otherParticle.y - mouseRef.current.y;
            const mouseDistance1 = Math.sqrt(mouseDx1 * mouseDx1 + mouseDy1 * mouseDy1);
            const mouseDistance2 = Math.sqrt(mouseDx2 * mouseDx2 + mouseDy2 * mouseDy2);

            // Base opacity based on distance
            const baseOpacity = (120 - distance) / 120 * 0.4;

            // Enhanced opacity when near cursor
            let enhancedOpacity = baseOpacity;
            let hasGlow = false;

            if (mouseDistance1 < 150 || mouseDistance2 < 150) {
              const mouseInfluence = Math.max(
                Math.max(0, 1 - mouseDistance1 / 150),
                Math.max(0, 1 - mouseDistance2 / 150)
              );
              enhancedOpacity = Math.min(0.8, baseOpacity + mouseInfluence * 0.4);
              hasGlow = true;
            }

            connections.push({
              x1: particle.x,
              y1: particle.y,
              x2: otherParticle.x,
              y2: otherParticle.y,
              opacity: enhancedOpacity,
              hasGlow: hasGlow,
              distance: distance
            });
          }
        }
      }

      // Sort connections by opacity (draw brighter ones last for better visual effect)
      connections.sort((a, b) => a.opacity - b.opacity);

      // Draw all connections
      connections.forEach(connection => {
        ctx.beginPath();
        ctx.moveTo(connection.x1, connection.y1);
        ctx.lineTo(connection.x2, connection.y2);

        // Apply glow effect if needed
        if (connection.hasGlow) {
          ctx.shadowBlur = 4;
          ctx.shadowColor = theme === 'dark' ? 'rgba(255, 255, 255, 0.4)' : 'rgba(37, 99, 235, 0.4)';
        }

        ctx.strokeStyle = theme === 'dark' ? 
          `rgba(255, 255, 255, ${connection.opacity})` : 
          `rgba(37, 99, 235, ${connection.opacity})`;
        ctx.lineWidth = connection.hasGlow ? 2 : 1;
        ctx.stroke();

        // Reset shadow
        ctx.shadowBlur = 0;
      });

      animationRef.current = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      window.removeEventListener('mousemove', handleMouseMove);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [theme]);

  const scrollToAbout = () => {
    const element = document.getElementById('about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section className="hero-section">
      <canvas
        ref={canvasRef}
        className="particles-canvas"
      />

      <div className="hero-content">
        <motion.h1
          className="hero-name"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Nyan Aung
        </motion.h1>

        <motion.div
          className="hero-subtitle"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <TypewriterText 
            texts={[
              "Full-Stack Engineer",
              "Software Engineer",
              "Backend Engineer",
              "Frontend Engineer"
            ]}
            speed={80}
            deleteSpeed={40}
            pauseTime={2000}
          />
        </motion.div>
      </div>

      <motion.div
        className="scroll-indicator"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.5 }}
        onClick={scrollToAbout}
      >
        <motion.div
          className="scroll-arrow"
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          â†“
        </motion.div>
      </motion.div>

      <style jsx>{`
        .hero-section {
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          position: relative;
          background: linear-gradient(135deg, var(--gradient-start) 0%, var(--gradient-end) 100%);
          overflow: hidden;
        }

        .particles-canvas {
          position: absolute !important;
          top: 0 !important;
          left: 0 !important;
          width: 100% !important;
          height: 100% !important;
          z-index: 1 !important;
          pointer-events: none;
        }

        .hero-content {
          text-align: center;
          z-index: 10;
          position: relative;
        }

        .hero-name {
          font-family: 'Poppins', sans-serif;
          font-size: 80px;
          font-weight: 700;
          color: var(--text-light);
          margin-bottom: 20px;
          background: linear-gradient(45deg, var(--text-light), var(--accent-blue));
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .hero-subtitle {
          font-family: 'Inter', sans-serif;
          font-size: 24px;
          font-weight: 300;
          color: var(--text-light);
          opacity: 0.8;
          max-width: 600px;
          margin: 0 auto;
          min-height: 30px;
        }

        .typewriter-text {
          display: inline-block;
        }

        .cursor {
          animation: blink 1s infinite;
          color: var(--accent-blue);
          font-weight: 400;
        }

        @keyframes blink {
          0%, 50% { opacity: 1; }
          51%, 100% { opacity: 0; }
        }

        .scroll-indicator {
          position: absolute;
          bottom: 30px;
          left: 50%;
          transform: translateX(-50%);
          cursor: pointer;
          color: var(--text-light);
          opacity: 0.7;
        }

        .scroll-arrow {
          font-size: 24px;
          font-weight: 300;
        }

        @media (max-width: 768px) {
          .hero-name {
            font-size: 50px;
          }

          .hero-subtitle {
            font-size: 18px;
            padding: 0 20px;
          }

          .scroll-indicator {
            bottom: 20px;
          }

          .scroll-arrow {
            font-size: 20px;
          }
        }

        @media (max-width: 480px) {
          .hero-name {
            font-size: 40px;
          }

          .hero-subtitle {
            font-size: 16px;
          }
        }
      `}</style>
    </section>
  );
};

export default Hero;
