import React from 'react';
import { motion } from 'framer-motion';
import { 
  SiPython, SiJavascript, SiTypescript, SiJava, SiCplusplus, SiC, SiHtml5, SiCss3, SiPostgresql, SiMongodb,
  SiReact, SiNodedotjs, SiExpress, SiDjango, SiDocker, SiLinux, SiWebsocket, SiJsonwebtokens, SiCloudinary,
  SiMongoose, SiGithub, SiGit, SiPostman, SiInsomnia, SiJest, SiGithubactions
} from 'react-icons/si';
import { FaNetworkWired } from 'react-icons/fa';

const Skills = ({ theme }) => {
  return (
    <section id="skills" className="skills-section">
      <motion.div 
        className="skills-container"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8, ease: 'easeOut' }}
        viewport={{ once: false, margin: '-100px' }}
      >
        <motion.h2
          className="about-title text-shadows"
          initial={{ opacity: 0, scale: 0.9 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.6, delay: 0.2, ease: 'easeOut' }}
          viewport={{ once: false }}
        >
          Technical Skills
        </motion.h2>

        <div className="skills-grid">
          <div className="skills-category">
            <h3 className="skills-category-title">Languages</h3>
            <div className="skills-items">
              <div className="skill" data-tip="Python"><SiPython /></div>
              <div className="skill" data-tip="JavaScript"><SiJavascript /></div>
              <div className="skill" data-tip="TypeScript"><SiTypescript /></div>
              <div className="skill" data-tip="Java"><SiJava /></div>
              <div className="skill" data-tip="C++"><SiCplusplus /></div>
              <div className="skill" data-tip="C"><SiC /></div>
              <div className="skill" data-tip="HTML"><SiHtml5 /></div>
              <div className="skill" data-tip="CSS"><SiCss3 /></div>
              <div className="skill" data-tip="SQL"><SiPostgresql /></div>
            </div>
          </div>

          <div className="skills-category">
            <h3 className="skills-category-title">Frameworks</h3>
            <div className="skills-items">
              <div className="skill" data-tip="React"><SiReact /></div>
              <div className="skill" data-tip="Node.js"><SiNodedotjs /></div>
              <div className="skill" data-tip="Express"><SiExpress /></div>
              <div className="skill" data-tip="Django"><SiDjango /></div>
              <div className="skill" data-tip="Django Channels"><SiDjango /></div>
            </div>
          </div>

          <div className="skills-category">
            <h3 className="skills-category-title">Developer Tools</h3>
            <div className="skills-items">
              <div className="skill" data-tip="Git"><SiGit /></div>
              <div className="skill" data-tip="GitHub"><SiGithub /></div>
              <div className="skill" data-tip="Linux"><SiLinux /></div>
              <div className="skill" data-tip="Docker"><SiDocker /></div>
              <div className="skill" data-tip="MongoDB"><SiMongodb /></div>
              <div className="skill" data-tip="PostgreSQL"><SiPostgresql /></div>
              <div className="skill" data-tip="WebSockets"><SiWebsocket /></div>
              <div className="skill" data-tip="JWT/Auth"><SiJsonwebtokens /></div>
              <div className="skill" data-tip="Cloudinary"><SiCloudinary /></div>
              <div className="skill" data-tip="REST APIs"><FaNetworkWired /></div>
              <div className="skill" data-tip="Testing"><SiJest /></div>
              <div className="skill" data-tip="CI/CD"><SiGithubactions /></div>
              <div className="skill" data-tip="Postman"><SiPostman /></div>
              <div className="skill" data-tip="Insomnia"><SiInsomnia /></div>
            </div>
          </div>

          <div className="skills-category">
            <h3 className="skills-category-title">Libraries</h3>
            <div className="skills-items">
              <div className="skill" data-tip="Mongoose"><SiMongoose /></div>
              <div className="skill" data-tip="Express"><SiExpress /></div>
              <div className="skill" data-tip="Django Channels"><SiDjango /></div>
            </div>
          </div>
        </div>
      </motion.div>

      <style jsx>{`
        .skills-section {
          min-height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          padding: 100px 0;
          background: ${theme === 'dark' ? 'linear-gradient(135deg, #042a1c 0%, #042a1c 100%)' : 'linear-gradient(135deg, #EEEEEE 0%, #EEEEEE 100%)'};
          transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
        }
        .skills-container { max-width: 1200px; width: 100%; padding: 0 20px; }
        .about-title { color: ${theme === 'dark' ? '#e3e5e6' : '#000000'}; }
        .text-shadows {
          text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}, 6px 6px 0 ${theme === 'dark' ? '#1a5f4a' : '#4b5563'}, 9px 9px 0 ${theme === 'dark' ? '#2d6b58' : '#6b7280'}, 12px 12px 0 ${theme === 'dark' ? '#407766' : '#9ca3af'};
          font-family: 'Poppins', sans-serif;
          font-weight: 700;
          text-transform: uppercase;
          font-size: calc(2rem + 4vw);
          text-align: center;
          margin: 0 0 40px 0;
          color: ${theme === 'dark' ? '#e3e5e6' : '#000000'};
          animation: shadows 1.2s ease-in infinite;
          letter-spacing: 0.2rem;
        }
        @keyframes shadows {
          0% { text-shadow: none; }
          10% { transform: translate(-3px, -3px); text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}; }
          20% { transform: translate(-6px, -6px); text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}, 6px 6px 0 ${theme === 'dark' ? '#1a5f4a' : '#4b5563'}; }
          30% { transform: translate(-9px, -9px); text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}, 6px 6px 0 ${theme === 'dark' ? '#1a5f4a' : '#4b5563'}, 9px 9px 0 ${theme === 'dark' ? '#2d6b58' : '#6b7280'}; }
          40% { transform: translate(-12px, -12px); text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}, 6px 6px 0 ${theme === 'dark' ? '#1a5f4a' : '#4b5563'}, 9px 9px 0 ${theme === 'dark' ? '#2d6b58' : '#6b7280'}, 12px 12px 0 ${theme === 'dark' ? '#407766' : '#9ca3af'}; }
          50% { transform: translate(-12px, -12px); text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}, 6px 6px 0 ${theme === 'dark' ? '#1a5f4a' : '#4b5563'}, 9px 9px 0 ${theme === 'dark' ? '#2d6b58' : '#6b7280'}, 12px 12px 0 ${theme === 'dark' ? '#407766' : '#9ca3af'}; }
          60% { text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}, 6px 6px 0 ${theme === 'dark' ? '#1a5f4a' : '#4b5563'}, 9px 9px 0 ${theme === 'dark' ? '#2d6b58' : '#6b7280'}, 12px 12px 0 ${theme === 'dark' ? '#407766' : '#9ca3af'}; }
          70% { text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}, 6px 6px 0 ${theme === 'dark' ? '#1a5f4a' : '#4b5563'}, 9px 9px 0 ${theme === 'dark' ? '#2d6b58' : '#6b7280'}; }
          80% { text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}, 6px 6px 0 ${theme === 'dark' ? '#1a5f4a' : '#4b5563'}; }
          90% { text-shadow: 3px 3px 0 ${theme === 'dark' ? '#0d4f3c' : '#374151'}; }
          100% { text-shadow: none; }
        }
        .skills-grid { display: grid; grid-template-columns: repeat(2, minmax(0, 1fr)); gap: 40px; }
        .skills-category { background: ${theme === 'dark' ? 'rgba(227, 229, 230, 0.04)' : 'rgba(0, 0, 0, 0.04)'}; border: 1px solid ${theme === 'dark' ? 'rgba(227, 229, 230, 0.15)' : 'rgba(0, 0, 0, 0.08)'}; border-radius: 16px; padding: 24px; box-shadow: ${theme === 'dark' ? '0 10px 30px rgba(0,0,0,0.35)' : '0 10px 20px rgba(0,0,0,0.08)'}; }
        .skills-category-title { font-family: 'Poppins', sans-serif; font-size: 20px; font-weight: 700; margin-bottom: 16px; color: ${theme === 'dark' ? '#e3e5e6' : '#000000'}; }
        .skills-items { display: grid; grid-template-columns: repeat(7, 1fr); gap: 16px; }
        .skill { height: 60px; width: 60px; display: inline-flex; align-items: center; justify-content: center; border-radius: 14px; background: ${theme === 'dark' ? 'rgba(227, 229, 230, 0.06)' : 'rgba(0, 0, 0, 0.06)'}; border: 1px solid ${theme === 'dark' ? 'rgba(227, 229, 230, 0.18)' : 'rgba(0, 0, 0, 0.08)'}; color: ${theme === 'dark' ? '#e3e5e6' : '#000000'}; position: relative; transition: transform 0.25s ease, box-shadow 0.25s ease, background 0.25s ease; box-shadow: ${theme === 'dark' ? '0 6px 16px rgba(0,0,0,0.35)' : '0 6px 12px rgba(0,0,0,0.08)'}; }
        .skill:hover { transform: translateY(-6px) rotate(-3deg); background: ${theme === 'dark' ? 'rgba(227, 229, 230, 0.1)' : 'rgba(0, 0, 0, 0.1)'}; box-shadow: ${theme === 'dark' ? '0 10px 24px rgba(0,0,0,0.45), 0 0 12px rgba(227,229,230,0.25) inset' : '0 10px 20px rgba(0,0,0,0.15)'}; }
        .skill :global(svg) { width: 28px; height: 28px; }
        .skill::after { content: attr(data-tip); position: absolute; bottom: -32px; left: 50%; transform: translateX(-50%); white-space: nowrap; background: ${theme === 'dark' ? 'rgba(0, 0, 0, 0.6)' : 'rgba(0, 0, 0, 0.8)'}; color: #ffffff; padding: 4px 8px; border-radius: 6px; font-size: 11px; opacity: 0; pointer-events: none; transition: opacity 0.2s ease, transform 0.2s ease; }
        .skill:hover::after { opacity: 1; transform: translateX(-50%) translateY(-2px); }

        @media (max-width: 1024px) { .skills-grid { grid-template-columns: 1fr; } .skills-items { grid-template-columns: repeat(6, 1fr); } }
        @media (max-width: 768px) { .skills-section { padding: 80px 0; } .skills-items { grid-template-columns: repeat(5, 1fr); } }
        @media (max-width: 480px) { .skills-items { grid-template-columns: repeat(4, 1fr); } }
      `}</style>
    </section>
  );
};

export default Skills;
